import React, { Fragment, useContext, useState, useEffect } from "react";
import { Dialog, Transition } from "@headlessui/react";
import { Link, Navigate } from "react-router-dom";
import { FiSun } from "react-icons/fi";
import { BsFillCloudSunFill } from "react-icons/bs";
import { RxCross2 } from "react-icons/rx";
import { useSelector } from "react-redux";
import SearchAndDisplay from "../search/SearchAndDisplay";

import Logo from "../../../public/images/logo.png";

function Navbar() {
  const [open, setOpen] = useState(false);
  const [showLoginTooltip, setShowLoginTooltip] = useState(false);

  const user = JSON.parse(localStorage.getItem("user"));
  const cartItems = useSelector((state) => state.cart);

  const logOut = () => {
    localStorage.clear("user");
    window.location.href = "/login";
  };

  // Handle showing the login reminder tooltip
  useEffect(() => {
    // Only show tooltip if user is not logged in
    if (!user) {
      // Show tooltip after 15 seconds
      const showTimer = setTimeout(() => {
        setShowLoginTooltip(true);

        // Auto-hide tooltip after 10 seconds
        const hideTimer = setTimeout(() => {
          setShowLoginTooltip(false);
        }, 10000);

        return () => clearTimeout(hideTimer);
      }, 15000);

      return () => clearTimeout(showTimer);
    }
  }, [user]);

  // Function to dismiss tooltip on user interaction
  const dismissTooltip = () => {
    setShowLoginTooltip(false);
  };

  // Add click listener to document to dismiss tooltip on any click
  useEffect(() => {
    if (showLoginTooltip) {
      document.addEventListener("click", dismissTooltip);
      document.addEventListener("keydown", dismissTooltip);

      return () => {
        document.removeEventListener("click", dismissTooltip);
        document.removeEventListener("keydown", dismissTooltip);
      };
    }
  }, [showLoginTooltip]);

  return (
    <div className="bg-white sticky top-0 z-50">
      <Transition.Root show={open} as={Fragment}>
        <Dialog as="div" className="relative z-40 lg:hidden" onClose={setOpen}>
          <Transition.Child
            as={Fragment}
            enter="transition-opacity ease-linear duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="transition-opacity ease-linear duration-300"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-25" />
          </Transition.Child>

          <div className="fixed inset-0 z-40 flex">
            <Transition.Child
              as={Fragment}
              enter="transition ease-in-out duration-300 transform"
              enterFrom="-translate-x-full"
              enterTo="translate-x-0"
              leave="transition ease-in-out duration-300 transform"
              leaveFrom="translate-x-0"
              leaveTo="-translate-x-full"
            >
              <Dialog.Panel className="relative flex w-full max-w-xs flex-col overflow-y-auto bg-white pb-12 shadow-xl">
                <div className="flex px-4 pb-2 pt-28">
                  <button
                    type="button"
                    className="-m-2 inline-flex items-center justify-center rounded-md p-2 text-gray-400"
                    onClick={() => setOpen(false)}
                  >
                    <span className="sr-only">Close menu</span>
                    <RxCross2 />
                  </button>
                </div>
                <div className="space-y-6 border-t border-gray-200 px-4 py-6">
                  <div className="flow-root">
                    <Link
                      to={"/"}
                      className="-m-2 block p-2 font-medium text-gray-900"
                    >
                      Home
                    </Link>
                  </div>
                  <div className="flow-root">
                    <Link
                      to={"/categories"}
                      className="-m-2 block p-2 font-medium text-gray-900"
                    >
                      Category
                    </Link>
                  </div>
                  <div className="flow-root">
                    <Link
                      to={"/allproducts"}
                      className="-m-2 block p-2 font-medium text-gray-900"
                    >
                      Product
                    </Link>
                  </div>
                  {user && (
                    <>
                      <div className="flow-root">
                        <Link
                          to={"/order"}
                          className="-m-2 block p-2 font-medium text-gray-900"
                        >
                          Order
                        </Link>
                      </div>
                      <div className="flow-root">
                        <Link
                          to={"/wishlist"}
                          className="-m-2 block p-2 font-medium text-gray-900"
                        >
                          Wishlist
                        </Link>
                      </div>
                    </>
                  )}

                  {user?.role === "admin" && (
                    <div className="flow-root">
                      <Link
                        to={"/dashboard"}
                        className="text-sm font-medium text-gray-700"
                      >
                        Admin
                      </Link>
                    </div>
                  )}

                  {user && (
                    <Link to="/userinfo">
                      <div>
                        <img
                          src={
                            user.photoURL ||
                            "data:image/jpeg;base64,"
                          }
                          className="w-12 h-12 rounded-full hover:cursor-pointer"
                          alt="User avatar"
                        />
                      </div>
                    </Link>
                  )}

                  <div className="flow-root">
                    <a
                      className="-m-2 block p-2 font-medium text-gray-900 cursor-pointer"
                      onClick={logOut}
                    >
                      {user ? "Logout" : "Login"}
                    </a>
                  </div>

                  <div>
                    <Link to="/customization">
                      <div className="px-6 py-3 text-white font-bold bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 rounded-lg shadow-lg hover:scale-105 hover:shadow-xl transition-transform duration-300 ease-in-out text-center uppercase tracking-wide">
                        Customize your order
                      </div>
                    </Link>
                  </div>
                </div>
              </Dialog.Panel>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition.Root>

      <header className="relative bg-white">
        <nav
          aria-label="Top"
          className="bg-gray-100 px-4 sm:px-6 lg:px-8 shadow-xl"
        >
          <div className="">
            <div className="flex h-16 items-center">
              <button
                type="button"
                className="rounded-md bg-white p-2 text-gray-400 lg:hidden"
                onClick={() => setOpen(true)}
              >
                <span className="sr-only">Open menu</span>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  strokeWidth="1.5"
                  stroke="currentColor"
                  className="w-6 h-6"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5"
                  />
                </svg>
              </button>

              {/* Logo */}
              <div className="ml-4 flex lg:ml-0">
                <Link to={"/"} className="flex">
                  <div className="flex">
                    <img src={Logo} className="w-32" />
                  </div>
                </Link>
              </div>

              <div className="ml-auto flex gap-5 items-center">
                <div className="hidden md:flex">
                  <Link to="/customization">
                    <div className="px-6 py-3 text-white font-bold bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 rounded-lg shadow-lg hover:scale-105 hover:shadow-xl transition-transform duration-300 ease-in-out text-center uppercase tracking-wide">
                      Customize your order
                    </div>
                  </Link>
                </div>

                <div className="">
                  <SearchAndDisplay />
                </div>

                <div className="hidden lg:flex lg:flex-1 lg:items-center lg:justify-end lg:space-x-6">
                  <Link to={"/"} className="text-sm font-medium text-gray-700">
                    Home
                  </Link>
                  <Link
                    to={"/categories"}
                    className="text-sm font-medium text-gray-700"
                  >
                    Category
                  </Link>
                  <Link
                    to={"/allproducts"}
                    className="text-sm font-medium text-gray-700"
                  >
                    Products
                  </Link>

                  {user && (
                    <>
                      <Link
                        to={"/order"}
                        className="text-sm font-medium text-gray-700"
                      >
                        Order
                      </Link>
                      <Link
                        to={"/wishlist"}
                        className="text-sm font-medium text-gray-700"
                      >
                        Wishlist
                      </Link>
                    </>
                  )}

                  {user?.role === "admin" && (
                    <Link
                      to={"/dashboard"}
                      className="text-sm font-medium text-gray-700"
                    >
                      Admin
                    </Link>
                  )}

                  {user && (
                    <Link to="/userinfo">
                      <div>
                        <img
                          src={
                            user.photoURL ||
                            "data:image/jpeg;base64,"
                          }
                          alt="User avatar"
                          className="w-8 h-8 rounded-full hover:cursor-pointer"
                        />
                      </div>
                    </Link>
                  )}

                  <a
                    className="text-sm font-medium text-gray-700 cursor-pointer"
                    onClick={logOut}
                  >
                    {user ? "Logout" : "Login"}
                  </a>
                </div>

                {/* Cart */}
                {user && (
                  <div className="ml-4 flow-root lg:ml-6">
                    <Link
                      to={"/cart"}
                      className="group -m-2 flex items-center p-2"
                    >
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                        strokeWidth={1.5}
                        stroke="currentColor"
                        className="w-6 h-6"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          d="M2.25 3h1.386c.51 0 .955.343 1.087.835l.383 1.437M7.5 14.25a3 3 0 00-3 3h15.75m-12.75-3h11.218c1.121-2.3 2.1-4.684 2.924-7.138a60.114 60.114 0 00-16.536-1.84M7.5 14.25L5.106 5.272M6 20.25a.75.75 0 11-1.5 0 .75.75 0 011.5 0zm12.75 0a.75.75 0 11-1.5 0 .75.75 0 011.5 0z"
                        />
                      </svg>
                      <span className="ml-2 text-sm font-medium text-gray-700 group-">
                        {cartItems.length}
                      </span>
                      <span className="sr-only">items in cart, view bag</span>
                    </Link>
                  </div>
                )}
              </div>
            </div>
          </div>
        </nav>
      </header>

      {/* Login Reminder Tooltip */}
      {showLoginTooltip && !user && (
        <div className="fixed top-20 right-4 z-50 bg-gradient-to-r from-blue-500 to-purple-500 text-white px-6 py-4 rounded-lg shadow-xl max-w-xs animate-fadeIn">
          <button
            className="absolute top-2 right-2 text-white hover:text-gray-200"
            onClick={(e) => {
              e.stopPropagation();
              dismissTooltip();
            }}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-4 w-4"
              viewBox="0 0 20 20"
              fill="currentColor"
            >
              <path
                fillRule="evenodd"
                d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
                clipRule="evenodd"
              />
            </svg>
          </button>
          <h3 className="font-bold text-lg mb-2">Welcome to Printi!</h3>
          <p className="mb-4">
            Log in to access personalized features like orders, wishlist, and
            customization.
          </p>
          <Link to="/login">
            <button className="bg-white text-purple-600 font-bold py-2 px-4 rounded-lg hover:bg-gray-100 transition-colors duration-300 w-full">
              Log In Now
            </button>
          </Link>
        </div>
      )}
    </div>
  );
}

// Add CSS for animation
const style = document.createElement("style");
style.innerHTML = `
  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(-10px); }
    to { opacity: 1; transform: translateY(0); }
  }
  .animate-fadeIn {
    animation: fadeIn 0.5s ease-out forwards;
  }
`;
document.head.appendChild(style);

export default Navbar;
